mapper.select:
select * from tb_sw 
where swname like #{keyword}
where swtype like #{keyword}
where maker like #{keyword}

select count(*) from tb_sw
where swname like #{keyword}
where swtype like #{keyword}
where maker like #{keyword}

select count(*) from tb_equipment
where equipmenttype like #{keyword}
where employeenumber like #{keyword}
where equipmentid like #{keyword}

select * from tb_equipment 
where equipmenttype like #{keyword}
where employeenumber like #{keyword}
where equipmentid like #{keyword}

select e.equipmentid,e.swnumber,s.swname,s.swversion 
	from tb_equipsw e join tb_sw s on e.swnumber=s.swnumber
		where e.equipmentid=#{equipmentid};
